import React, { useEffect, useState } from 'react';
const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || '';

const CandidateList = () => {
  const [applications, setApplications] = useState([]);
  const [editing, setEditing] = useState({});
  const [remarks, setRemarks] = useState({});
  const [selected, setSelected] = useState({});

  useEffect(() => {
    fetch(`${API_BASE_URL}/api/jobs/applications`)
      .then(res => res.json())
      .then(data => setApplications(data));
  }, []);

  const handleEdit = (id, currentRemarks) => {
    setEditing({ ...editing, [id]: true });
    setRemarks({ ...remarks, [id]: currentRemarks || '' });
  };

  const handleSave = async (id) => {
    await fetch(`${API_BASE_URL}/api/jobs/applications/${id}/remarks`, {
      method: 'PATCH',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ remarks: remarks[id] }),
    });
    setEditing({ ...editing, [id]: false });
    // Refresh list
    fetch(`${API_BASE_URL}/api/jobs/applications`)
      .then(res => res.json())
      .then(data => setApplications(data));
  };

  // Accept/Reject handlers
  const handleStatus = async (id, status) => {
    await fetch(`${API_BASE_URL}/api/jobs/applications/${id}/status`, {
      method: 'PATCH',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ status }),
    });
    // Refresh list
    fetch(`${API_BASE_URL}/api/jobs/applications`)
      .then(res => res.json())
      .then(data => setApplications(data));
  };

  const toggleSelect = (id) => {
    setSelected(prev => ({ ...prev, [id]: !prev[id] }));
  };

  const selectAll = (checked) => {
    if (!checked) {
      setSelected({});
      return;
    }
    const all = {};
    applications.forEach(app => { all[app._id] = true; });
    setSelected(all);
  };

  const openGmailWithRecipients = () => {
    const selectedEmails = applications
      .filter(app => selected[app._id])
      .map(app => app.email)
      .join(',');

    if (selectedEmails.length === 0) {
      alert('Please select at least one candidate.');
      return;
    }

    window.open(`https://mail.google.com/mail/?view=cm&fs=1&to=${encodeURIComponent(selectedEmails)}`, '_blank');
  };

  const selectedCount = Object.values(selected).filter(Boolean).length;

  return (
    <div
      style={{
        maxWidth: 1200,
        margin: '40px auto',
        background: '#fff',
        padding: 32,
        borderRadius: 8,
        boxShadow: '0 2px 8px #eee',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        textAlign: 'center'
      }}
    >
      <button
        style={{ float: 'right', margin: '16px' }}
        onClick={() => {
          localStorage.removeItem('isUser');
          window.location.href = 'http://localhost:3000/admin-login';
        }}
      >
        Logout
      </button>
      <h2>Candidate List</h2>
      <div style={{ width: '100%', marginBottom: 16, textAlign: 'left' }}>
        {selectedCount > 0 && (
          <div style={{ display: 'flex', justifyContent: 'flex-end' }}>
            <button
              onClick={openGmailWithRecipients}
              style={{ 
                background: '#4CAF50', 
                color: '#fff', 
                border: 'none', 
                padding: '8px 16px', 
                borderRadius: 4, 
                cursor: 'pointer',
                display: 'flex',
                alignItems: 'center',
                gap: '8px'
              }}
            >
              <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M4 4h16c1.1 0 2 .9 2 2v12c0 1.1-.9 2-2 2H4c-1.1 0-2-.9-2-2V6c0-1.1.9-2 2-2z"></path>
                <polyline points="22,6 12,13 2,6"></polyline>
              </svg>
              Email Selected with Gmail
            </button>
          </div>
        )}
      </div>
      <table border="1" cellPadding="8" style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th style={{ width: 60 }}>
              <label style={{ display: 'flex', alignItems: 'center', gap: 6, justifyContent: 'center' }}>
                <input
                  type="checkbox"
                  onChange={(e) => selectAll(e.target.checked)}
                  checked={applications.length > 0 && applications.every(a => selected[a._id])}
                />
                Select
              </label>
            </th>
            {[
              'Name',
              'City',
              'Email',
              'Mobile',
              'Qualification',
              'Skills',
              'Experience',
              'Current/Last Salary',
              'Current/Last Post',
              'Interested Job Post',
              'Expected Salary',
              'Interested Job Locations',
              'Reference From',
              'CV',
              'Remarks',
              'Action',
            ].map((label) => (
              <th key={label}>{label}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {applications.map((app) => (
            <tr key={app._id}>
              <td style={{ textAlign: 'center' }}>
                <input
                  type="checkbox"
                  checked={!!selected[app._id]}
                  onChange={() => toggleSelect(app._id)}
                />
              </td>
              <td style={{ 
  padding: '12px', 
  whiteSpace: 'nowrap',
  textOverflow: 'ellipsis',
  overflow: 'hidden',
  maxWidth: '200px' // Adjust as needed
}}>
  {app.name}
</td>
              <td>{app.city}</td>
              <td>{app.email}</td>
              <td>{app.mobile}</td>
              <td>{app.qualification}</td>
              <td>{app.skills}</td>
              <td>{app.experience}</td>
              <td>{app.currentSalary}</td>
              <td>{app.currentPost}</td>
              <td>{app.interestedJobPost}</td>
              <td>{app.expectedSalary}</td>
              <td>{app.interestedJobLocations}</td>
              <td>{app.referenceFrom}</td>
              <td>
                {app.cvUrl ? (
                  <a
                    href={`${API_BASE_URL}/api/jobs/view/${app.cvUrl.replace(/^uploads[\\/]/, '')}`}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    View CV
                  </a>
                ) : (
                  'No CV uploaded'
                )}
              </td>
              <td>
                {editing[app._id] ? (
                  <>
                    <input
                      value={remarks[app._id] || ''}
                      onChange={e => setRemarks({ ...remarks, [app._id]: e.target.value })}
                    />
                    <button onClick={() => handleSave(app._id)}>Save</button>
                  </>
                ) : (
                  <>
                    {app.remarks || ''}
                    <button 
                      style={{ 
                        marginLeft: 8, 
                        background: '#f0f0f0',
                        border: '1px solid #ccc',
                        padding: '2px 8px',
                        borderRadius: 4,
                        cursor: 'pointer'
                      }} 
                      onClick={() => handleEdit(app._id, app.remarks)}
                    >
                      Edit
                    </button>
                  </>
                )}
              </td>
              <td>
                <button
                  style={{ 
                    marginRight: 8, 
                    background: '#4caf50', 
                    color: '#fff', 
                    border: 'none', 
                    padding: '4px 12px', 
                    borderRadius: 4, 
                    cursor: 'pointer' 
                  }}
                  onClick={() => handleStatus(app._id, 'accepted')}
                >
                  Accept
                </button>
                <button
                  style={{ 
                    background: '#f44336', 
                    color: '#fff', 
                    border: 'none', 
                    padding: '4px 12px', 
                    borderRadius: 4, 
                    cursor: 'pointer' 
                  }}
                  onClick={() => handleStatus(app._id, 'rejected')}
                >
                  Reject
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CandidateList;
