import React, { useEffect, useState } from 'react';
const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || '';

const CandidateList = () => {
  const [applications, setApplications] = useState([]);
  const [editing, setEditing] = useState({});
  const [remarks, setRemarks] = useState({});
  const [selected, setSelected] = useState({});
  const [subject, setSubject] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetch(`${API_BASE_URL}/api/jobs/applications`)
      .then(res => res.json())
      .then(data => setApplications(data));
  }, []);

  const handleEdit = (id, currentRemarks) => {
    setEditing({ ...editing, [id]: true });
    setRemarks({ ...remarks, [id]: currentRemarks || '' });
  };

  const handleSave = async (id) => {
    await fetch(`${API_BASE_URL}/api/jobs/applications/${id}/remarks`, {
      method: 'PATCH',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ remarks: remarks[id] }),
    });
    setEditing({ ...editing, [id]: false });
    // Refresh list
    fetch(`${API_BASE_URL}/api/jobs/applications`)
      .then(res => res.json())
      .then(data => setApplications(data));
  };

  // Accept/Reject handlers
  const handleStatus = async (id, status) => {
    await fetch(`${API_BASE_URL}/api/jobs/applications/${id}/status`, {
      method: 'PATCH',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ status }),
    });
    // Refresh list
    fetch(`${API_BASE_URL}/api/jobs/applications`)
      .then(res => res.json())
      .then(data => setApplications(data));
  };

  const toggleSelect = (id) => {
    setSelected(prev => ({ ...prev, [id]: !prev[id] }));
  };

  const selectAll = (checked) => {
    if (!checked) {
      setSelected({});
      return;
    }
    const all = {};
    applications.forEach(app => { all[app._id] = true; });
    setSelected(all);
  };

  const sendBulkEmail = async () => {
    const selectedIds = Object.keys(selected).filter(id => selected[id]);
    if (selectedIds.length === 0) {
      alert('Please select at least one candidate.');
      return;
    }
    if (!subject.trim() || !message.trim()) {
      alert('Please enter subject and message.');
      return;
    }
    try {
      const res = await fetch(`${API_BASE_URL}/api/jobs/applications/bulk-email`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ids: selectedIds, subject, message })
      });
      // Handle non-JSON responses gracefully
      let data;
      try {
        data = await res.json();
      } catch (e) {
        data = {};
      }
      if (res.ok) {
        alert(`Emails sent successfully to ${data.sent || selectedIds.length} candidate(s).`);
        setSelected({});
        setSubject('');
        setMessage('');
      } else {
        if (res.status === 503) {
          alert('Email service not configured. Please set SMTP credentials.');
        } else {
          alert(data.error || 'Failed to send emails');
        }
      }
    } catch (err) {
      console.error(err);
      alert('Failed to send emails.');
    }
  };

  const handleEmailClick = (email) => {
    window.location.href = `mailto:${email}`;
  };

  return (
    <div
      style={{
        maxWidth: 1200,
        margin: '40px auto',
        background: '#fff',
        padding: 32,
        borderRadius: 8,
        boxShadow: '0 2px 8px #eee',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        textAlign: 'center'
      }}
    >
      <button
        style={{ float: 'right', margin: '16px' }}
        onClick={() => {
          localStorage.removeItem('isUser'); // or 'isAdmin' for admin panel
          window.location.href = 'http://localhost:3000/admin-login'; // redirect to Dashboard after logout
        }}
      >
        Logout
      </button>
      <h2>Candidate List</h2>
      <div style={{ width: '100%', marginBottom: 16, textAlign: 'left' }}>
        <div style={{ display: 'flex', gap: 12, alignItems: 'center' }}>
          <input
            type="text"
            placeholder="Email subject"
            value={subject}
            onChange={(e) => setSubject(e.target.value)}
            style={{ flex: 1, padding: 8 }}
          />
          <button
            onClick={sendBulkEmail}
            style={{ background: '#1976d2', color: '#fff', border: 'none', padding: '8px 16px', borderRadius: 4, cursor: 'pointer' }}
          >
            Send Email to Selected
          </button>
        </div>
        <textarea
          placeholder="Email message"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          rows={4}
          style={{ width: '100%', marginTop: 8, padding: 8 }}
        />
      </div>
      <table border="1" cellPadding="8" style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th style={{ width: 60 }}>
              <label style={{ display: 'flex', alignItems: 'center', gap: 6, justifyContent: 'center' }}>
                <input
                  type="checkbox"
                  onChange={(e) => selectAll(e.target.checked)}
                  checked={applications.length > 0 && applications.every(a => selected[a._id])}
                />
                Select
              </label>
            </th>
            {[
              'Name',
              'City',
              'Email',
              'Mobile',
              'Qualification',
              'skills',
              'Experience',
              'current/Last Salary',
              'current/Last Post',
              'Interested Job Post',
              'Expected Salary',
              'Interested Job Locations',
              'Reference From',
              'CV',
              'Remarks',
              'Action',
              'Email'
            ].map((label) => (
              <th key={label}>{label}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {applications.map((app) => (
            <tr key={app._id}>
              <td style={{ textAlign: 'center' }}>
                <input
                  type="checkbox"
                  checked={!!selected[app._id]}
                  onChange={() => toggleSelect(app._id)}
                />
              </td>
              <td>{app.name}</td>
              <td>{app.city}</td>
              <td>{app.email}</td>
              <td>{app.mobile}</td>
              <td>{app.qualification}</td>
              <td>{app.skills}</td>
              <td>{app.experience}</td>
              <td>{app.currentSalary}</td>
              <td>{app.currentPost}</td>
              <td>{app.interestedJobPost}</td>
              <td>{app.expectedSalary}</td>
              <td>{app.interestedJobLocations}</td>
              <td>{app.referenceFrom}</td>
              <td>
                {app.cvUrl ? (
                  <a
                    href={`${API_BASE_URL}/api/jobs/view/${app.cvUrl.replace(/^uploads[\\/]/, '')}`}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    View CV
                  </a>
                ) : (
                  'No CV uploaded'
                )}
              </td>
              <td>
                {editing[app._id] ? (
                  <>
                    <input
                      value={remarks[app._id] || ''}
                      onChange={e => setRemarks({ ...remarks, [app._id]: e.target.value })}
                    />
                    <button onClick={() => handleSave(app._id)}>Save</button>
                  </>
                ) : (
                  <>
                    {app.remarks || ''}
                    <button style={{ marginLeft: 8 }} onClick={() => handleEdit(app._id, app.remarks)}>Edit</button>
                  </>
                )}
              </td>
              <td>
                <button
                  style={{ marginRight: 8, background: '#4caf50', color: '#fff', border: 'none', padding: '4px 12px', borderRadius: 4, cursor: 'pointer' }}
                  onClick={() => handleStatus(app._id, 'accepted')}
                >
                  Accept
                </button>
                <button
                  style={{ background: '#f44336', color: '#fff', border: 'none', padding: '4px 12px', borderRadius: 4, cursor: 'pointer' }}
                  onClick={() => handleStatus(app._id, 'rejected')}
                >
                  Reject
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CandidateList;
